import React, { Component } from 'react';

class Card extends React.Component {
  constructor(props) {
    super(props);

    // Define inline styles
    this.styles = { backgroundColor: props.data.color || '#000' };
  }

  render() {
    console.log(this.props.data);
    const { title, author } = this.props.data;

    return (
      <div className="card-body" style={this.styles}>
        <div className="icon-wrapper section">
          <span className="icon">
          </span>
        </div>
        <div className="text-wrapper section">
          <div className="title"><span>{ title }</span></div>
          <div className="details"><span>{ author }</span></div>
          <Button color="success" size="sm" className="mr-2" onClick={this.editBook.bind(this, book.id, book.title, book.rating)}>Edit</Button>
            <Button color="danger" size="sm" onClick={this.deleteBook.bind(this, book.id)}>Delete</Button>
        </div>
      </div>
    )
  }
}

export default Card;
